import React, { Component } from 'react';
import axios from 'axios';
import classes from './Employee.module.css';
class EmployeePage extends Component {
  state = {
    EmployeeInfo: null,
  }
  componentDidMount() {
    axios.get(`https://employees-eb181-default-rtdb.firebaseio.com/employees.json/${this.props.match.params.id}`)
      .then(response => {
        const EmployeesData = response.data;
        this.setState({ EmployeeInfo: EmployeesData });
        console.log(this.state.EmployeeInfo);
      })
  }
  render() {
    let EmployeeRander = null;
    if (this.state.EmployeeInfo) {
      EmployeeRander = (<div className={classes.Employee}>
        <div className={classes.Rounded}>  <img alt="user" src={EmployeeInfo.avatar} /></div>
        <h2> {EmployeeInfo.first_name}  {EmployeeInfo.last_name}</h2>
        <h2>{"B00" + EmployeeInfo.id}</h2>
        <p>Email: {EmployeeInfo.email}</p>
      </div>);
    } else {
      EmployeeRander = <div><p>Loading...!</p></div>
    }
    console.log("what", EmployeeInfo);
    return (
      <>
        {EmployeeRander}
      </>
    )
  }
}

export default EmployeePage;
